import React from 'react';
import { FaRobot, FaVideo, FaChartBar, FaComments, FaDatabase, FaLock } from 'react-icons/fa';

function FeatureCard({ icon: Icon, title, description }) {
  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <Icon className="text-4xl text-blue-600 mb-4" />
      <h2 className="text-2xl font-semibold mb-2">{title}</h2>
      <p>{description}</p>
    </div>
  );
}

function Features() {
  const features = [
    {
      icon: FaRobot,
      title: "AI Interviewer",
      description: "Practice with our advanced AI interviewer that adapts to your responses and provides realistic interview scenarios."
    },
    {
      icon: FaVideo,
      title: "Video Analysis",
      description: "Get detailed feedback on your body language, facial expressions, and speech patterns to improve your overall presentation."
    },
    {
      icon: FaChartBar,
      title: "Performance Tracking",
      description: "Track your progress over time with detailed analytics and see how you improve with each practice session."
    },
    {
      icon: FaComments,
      title: "Custom Feedback",
      description: "Receive personalized feedback and tips based on your industry and the specific job roles you're targeting."
    },
    {
      icon: FaDatabase,
      title: "Question Bank",
      description: "Access a vast database of interview questions tailored to various industries and job positions."
    },
    {
      icon: FaLock,
      title: "Secure Platform",
      description: "Your data and practice sessions are kept completely confidential with our secure, encrypted platform."
    }
  ];

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold text-center mb-12">Our Features</h1>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        {features.map((feature, index) => (
          <FeatureCard key={index} {...feature} />
        ))}
      </div>
    </div>
  );
}

export default Features;
